import React, { useEffect, useRef, useState } from "react";
import styled from "styled-components";
import { markerData } from "../data/markerData";

function GoogleMap({ center }) {
  const ref = useRef();
  const [map, setMap] = useState(null);
  const addSingleMarkers = (data) => {
    let positions = [];
    data.forEach((el) =>
      positions.push({ lat: parseFloat(el.lat), lng: parseFloat(el.lng) })
    );
    return positions.map(
      (position) => new window.google.maps.Marker({ position, map })
    );
  };
  useEffect(() => {
    const newMap = new window.google.maps.Map(ref.current, {
      center: center,
      zoom: 5,
      disableDefaultUI: true,
    });
    markerData.forEach((el) => {
      const marker = new window.google.map.Marker({
        position: { lat: el.lat, lng: el.lng },
      });
    });
    setMap(newMap);
  }, [center]);
  return <Main id="map" ref={ref} />;
}

export default GoogleMap;
const Main = styled.div`
  width: 100vw;
  height: 100%;
`;
