import React, { useEffect, useRef, useState } from "react";
import styled from "styled-components";
import { markerData } from "../data/markerData";

function GoogleMap({ center }) {
  const ref = useRef();
  const [map, setMap] = useState(null);
  const [markerPositions, setMarkerPositions] = useState([]);
  const addSingleMarkers = (positions) => {
    console.log(positions);
    positions.map(
      (position) => new window.google.maps.Marker({ position, map })
    );
  };
  useEffect(() => {
    markerData.forEach((data) =>
      setMarkerPositions((prev) => [...prev, { lat: data.lat, lng: data.lng }])
    );
  }, []);
  useEffect(() => {
    const newMap = new window.google.maps.Map(ref.current, {
      center: center,
      zoom: 5,
      disableDefaultUI: true,
    });
    addSingleMarkers(markerPositions);

    setMap(newMap);
  }, [center, addSingleMarkers, markerPositions]);
  return <Main id="map" ref={ref} />;
}

export default GoogleMap;
const Main = styled.div`
  width: 100vw;
  height: 100%;
`;
