import { Wrapper, Status } from "@googlemaps/react-wrapper";
import React, { useEffect, useRef, useState } from "react";
import styled from "styled-components";
function Map() {
  const ref = useRef();
  const [center, setCenter] = useState({ lat: 37.569227, lng: 126.9777256 });
  const zoom = 16;
  const [map, setMap] = useState(null);

  useEffect(() => {
    const newMap = new window.google.maps.Map(ref.current, {
      center: center,
      zoom: zoom,
    });

    setMap(newMap);
  }, [center]);
  const render = (status) => {
    if (status === Status.FAILURE) return <h1>로딩 실패</h1>;
    return <h1>로딩 중</h1>;
  };
  return (
    <>
      <Nav>
        <Logo>TABI MAP</Logo>
      </Nav>
      <MainContainer>
        <Wrapper
          apiKey={process.env.REACT_APP_GOOGLE_MAP_API_KEY}
          render={render}
        >
          <div id="map" ref={ref} style={{ width: "100vw", height: "100vh" }} />
        </Wrapper>
      </MainContainer>
    </>
  );
}

export default Map;
const MainContainer = styled.div`
  width: 100vw;
  height: 100vh;
`;
const GoogleMap = styled.div`
  width: 100vw;
  height: 100vh;
`;
const Nav = styled.div`
  position: fixed;
  top: 0;
  width: 100vw;
  display: flex;
  align-items: flex-start;
  justify-content: space-between;
  padding: 20px 0;
  background: #fff;
  border-bottom: 1px solid #a0a0a0;
`;
const Logo = styled.p`
  font-size: 30px;
  font-weight: 700;
  margin-left: 30px;
`;
